ering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, chapterId = Just 2, questionId = Just 12, iLink = Nothing, iMandatory = True, iRules = [], iAutoComplete = []}, sgLevel = 0, sgItems = [InfoFI {ifiDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, chapterId = Just 2, questionId = Just 12, iLink = Nothing, iMandatory = True, iRules = [], iAutoComplete = []}, ifiText = "Did you really check a service like fairsharing.org to verify this?"}]}],SimpleOption "Yes"]}]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, chapterId = Just 2, questionId = Just 13, iLink = Nothing, iMandatory = False, iRules = [], iAutoComplete = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "Will you consider re-usability of your data beyond your original purpose?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Just "<p class=\"question-description\">Adding more than the strict minimum metadata about your experiment will possibly allow more wide re-use of your data, with associated higher data citation rates. Please note that it is not easy for yourself to see all other ways in which others could be reusing your data.</p>", chapterId = Just 2, questionId = Just 13, iLink = Nothing, iMandatory = True, iRules = [], iAutoComplete = []}, chfiAvailableOptions = [SimpleOption "No, I will just document the bare minimum",DetailedOption NoNumbering "Yes, I will document more metadata than needed for reproducibility" [SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, chapterId = Just 2, questionId = Just 13, iLink = Nothing, iMandatory = True, iRules = [], iAutoComplete = []}, sgLevel = 0, sgItems = [SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, chapterId = Just 2, questionId = Just 14, iLink = Nothing, iMandatory = False, iRules = [], iAutoComplete = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "How will you balance the extra efforts with the potential for added reusability?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, chapterId = Just 2, questionId = Just 14, iLink = Nothing, iMandatory = True, iRules = [], iAutoComplete = []}, chfiAvailableOptions = [SimpleOption "I will see what I can do",SimpleOption "I will use preselected additional standard modules of metadata",SimpleOption "I will collect all metadata I can gather and document the data set beyond minimal standards"]}]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, chapterId = Just 2, questionId = Just 15, iLink = Nothing, iMandatory = False, iRules = [], iAutoComplete = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "Do you need to exchange your data with others?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, chapterId = Just 2, questionId = Just 15, iLink = Nothing, iMandatory = True, iRules = [], iAutoComplete = []}, chfiAvailableOptions = [SimpleOption "No",SimpleOption "Yes"]}]}]}]]}]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, chapterId = Just 2, questionId = Just 16, iLink = Nothing, iMandatory = False, iRules = [], iAutoComplete = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "Did you consider how to monitor data integrity?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Just "<p class=\"question-description\">Working with large amounts of heterogenous data in a larger research group has implications for the data integrity. How do you make sure every step of the workflow is done with the right version of the data? How do you handle the situation when a mistake is uncovered? Will you be able to redo the strict minimum data handling?</p>", chapterId = Just 2, questionId = Just 16, iLink = Nothing, iMandatory = True, iRules = [], iAutoComplete = []}, chfiAvailableOptions = [SimpleOption "Skip",DetailedOption NoNumbering "Explore" [SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, chapterId = Just 2, questionId = Just 16, iLink = Nothing, iMandatory = True, iRules = [], iAutoComplete = []}, sgLevel = 0, sgItems = [SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, chapterId = Just 2, questionId = Just 17, iLink = Nothing, iMandatory = False, iRules = [], iAutoComplete = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "Will you be keeping a master list with checksums of certified/correct/canonical/verified data?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Just "<p class=\"question-description\">Data corruption or mistakes can happen with large amounts of files or large files. Keeping a master list with data checksums can be helpful to prevent expensive mistakes. It can also be helpful to keep the sample list under version control forcing that all changes are well documented.</p>", chapterId = Just 2, questionId = Just 17, iLink = Nothing, iMandatory = True, iRules = [], iAutoComplete = []}, chfiAvailableOptions = [SimpleOption "No",SimpleOption "Yes"]}]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, chapterId = Just 2, questionId = Just 18, iLink = Nothing, iMandatory = False, iRules = [], iAutoComplete = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "Will you define a way to detect file or sample swaps, e.g. by measuring something independently?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, chapterId = Just 2, questionId = Just 18, iLink = Nothing, iMandatory = True, iRules = [], iAutoComplete = []}, chfiAvailableOptions = [SimpleOption "No",SimpleOption "Yes"]}]}]}]]}]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, chapterId = Just 2, questionId = Just 20, iLink = Nothing, iMandatory = False, iRules = [], iAutoComplete = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "Do all datasets you work with have a license?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Just "<p class=\"question-description\">It is not always clear to everyone in the project (ad outside) what can and can not be done with a data set. It is helpful to associate each data set with a license as early as possible in the project. A data license should ideally be as free as possible: any restriction like 'only for non-commercial use' or 'attribution required' may reduce the reusability and thereby the number of citations. If possible, use a computer-readable and computer actionable license.</p>", chapterId = Just 2, questionId = Just 20, iLink = Nothing, iMandatory = True, iRules = [], iAutoComplete = []}, chfiAvailableOptions = [SimpleOption "No",DetailedOption NoNumbering "Yes" [SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, chapterId = Just 2, questionId = Just 20, iLink = Nothing, iMandatory = True, iRules = [], iAutoComplete = []}, sgLevel = 0, sgItems = [SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, chapterId = Just 2, questionId = Just 19, iLink = Nothing, iMandatory = False, iRules = [], iAutoComplete = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "Will you store the licenses with the data at all time?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Just "<p class=\"question-description\">It is very likely that data will be moved and copied. At some point people may lose track of the origins. It can be helpful to have the licenses (of coarse as open as possible) stored in close association with the data.</p>", chapterId = Just 2, questionId = Just 19, iLink = Nothing, iMandatory = True, iRules = [], iAutoComplete = []}, chfiAvailableOptions = [SimpleOption "No",SimpleOption "Yes"]}]}]}]]}]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, chapterId = Just 2, questionId = Just 21, iLink = Nothing, iMandatory = False, iRules = [], iAutoComplete = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "How will you keep provenance?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Just "<p class=\"question-description\">To make your experiments reproducible, all steps in the data processing must be documented in detail. The software you used, including version number, all options and parameters. This information together for every step of the analysis is part of the so-called data provenance. There are more questions regarding this in the chapter on data processing and curation.</p>", chapterId = Just 2, questionId = Just 21, iLink = Nothing, iMandatory = True, iRules = [], iAutoComplete = []}, chfiAvailableOptions = [SimpleOption "All steps will be documented in an (electronic) lab notebook",SimpleOption "Our work flow system documents the provenance automatically and completely"]}]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, chapterId = Just 2, questionId = Just 22, iLink = Nothing, iMandatory = False, iRules = [], iAutoComplete = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "How will you do file naming and file organization?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Just "<p class=\"question-description\">Putting some thoughts into file naming can save a lot of trouble later.</p>", chapterId = Just 2, questionId = Just 22, iLink = Nothing, iMandatory = True, iRules = [], iAutoComplete = []}, chfiAvailableOptions = [SimpleOption "Skip",DetailedOption NoNumbering "Explore" [SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, chapterId = Just 2, questionId = Just 22, iLink = Nothing, iMandatory = True, iRules = [], iAutoComplete = []}, sgLevel = 0, sgItems = [SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, chapterId = Just 2, questionId = Just 23, iLink = Nothing, iMandatory = False, iRules = [], iAutoComplete = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "Did you make a SOP (Standard Operating Procedure) for file naming?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Just "<p class=\"question-description\">It can help if everyone in the project uses the same naming scheme.</p>", chapterId = Just 2, questionId = Just 23, iLink = Nothing, iMandatory = True, iRules = [], iAutoComplete = []}, chfiAvailableOptions = [SimpleOption "No",SimpleOption "Yes"]}]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, chapterId = Just 2, questionId = Just 24, iLink = Nothing, iMandatory = False, iRules = [], iAutoComplete = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "Will you be keeping the relationships between data clear in the file names?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Just "<p class=\"question-description\">Advice: Use the same identifiers for sample IDs etc throughout the entire project.</p>", chapterId = Just 2, questionId = Just 24, iLink = Nothing, iMandatory = True, iRules = [], iAutoComplete = []}, chfiAvailableOptions = [SimpleOption "No",SimpleOption "Yes"]}]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, chapterId = Just 2, questionId = Just 25, iLink = Nothing, iMandatory = False, iRules = [], iAutoComplete = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "Will all the metadata in the file names also be available in the proper metadata?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Just "<p class=\"question-description\">The file names are very useful as metadata for people involved in the project, but to computers they are just identifiers. To prevent accidents with e.g. renamed files metadata information should always also be available elsewhere and not only through the file name.</p>", chapterId = Just 2, questionId = Just 25, iLink = Nothing, iMandatory = True, iRules = [], iAutoComplete = []}, chfiAvailableOptions = [SimpleOption "No, the file names in the project are an essential part of the metadata",SimpleOption "Yes, all metadata is also explicitly available elsewhere"]}]}]}]]}]}]}]]}]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, chapterId = Just 2, questionId = Just 5, iLink = Nothing, iMandatory = False, iRules = [], iAutoComplete = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "Is the risk of information loss, leaks and vandalism acceptably low?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Just "<p class=\"question-description\">There are many factors that can contribute to the risk of information loss or information leaks. They are often part of the behavior of the people that are involved in the project, but can also be steered by properly planned infrastructure.</p>", chapterId = Just 2, questionId = Just 5, iLink = Nothing, iMandatory = True, iRules = [], iAutoComplete = []}, chfiAvailableOptions = [SimpleOption "Skip this set of questions",DetailedOption NoNumbering "Explore the factors" [SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, chapterId = Just 2, questionId = Just 5, iLink = Nothing, iMandatory =